import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
function Square(props){
    return (
        <button className = "square" onClick={props.onClick}>
            {props.value}
        </button>
    );
}

class Board extends React.Component { 
    handleClick(i){
        const squares = this.state.squares.slice();
        if(calculateWinner(squares) ||squares[i]){ //if all squares are filled or there is a winner, stop changing game states. 
            return;
        }
        squares[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
            squares:squares,
            xIsNext: !this.state.xIsNext,
        });
    }
  renderSquare(i) {
    return (
        <Square
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
        />
    );
  }

  render() {
    const winner = calculateWinner(this.state.squares);
    let status;
    if(winner){
        status = 'Winner: ' + winner;
    }else{
        status = 'Next player: ' +(this.state.xIsNext ? 'X' : 'O');
    }

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
            <Board squares = {current.squares}
            onClick ={(i) => this.handleClick(i)}
            />
        </div>
        <div className = "game-info">
            <div>{status}</div>
            <ol></ol>
        </div>
        
      </div>
    );
  }
}

class Game extends React.Component { //component
    constructor(props){
        super(props);
        this.state = {
            history:[{
                squares:Array(9).fill(null),
            }],
            xIsNext: true,
        };
    }
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}
function calculateWinner(squares) { //All possibilities for a winner to happen.
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  }

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);
